const DEFAULT_FILTER = {
  participants: [],
  themes: [],
  search: '',
  day: '',
}

export default {
  props: {
    initialItems: {
      type: Array,
      default: () => [...PROGRAM_ITEMS]
    },
    lang: {
      type: String,
      default: 'ru'
    },
    filterParticipants: {
      type: Array,
      default: () => []
    },
    filterThemes: {
      type: Array,
      default: () => []
    },
    filterDays: {
      type: Array,
      default: () => []
    },
    actionFilter: {
      type: String,
      default: ''
    },
    title: {
      type: String,
      default: ''
    },
    initialNextPageNumber: {
      type: Number,
      default: 0
    },
  },
  data() {
    return {
      items: JSON.parse(JSON.stringify(this.initialItems)),
      headerListLang: {
        ru: {
          timeStr: 'Время',
          address: 'Место',
          tags: 'Тематика',
          title: 'Название',
        },
        en: {
          timeStr: 'Time',
          address: 'Place',
          tags: 'Subject matter',
          title: 'Title',
        }
      },
      notFoundLang: {
        ru: 'Ничего не найдено',
        en: 'Nothing found'
      },
      filter: DEFAULT_FILTER,
      nextPageNumber: this.initialNextPageNumber,
      actionGet: '',
      intersectionObserver: null
    }
  },
  computed: {
    filterFormData() {

      const formData = new FormData()

      if (this.filter.day) {
        formData.append('date', this.filter.day)
      }
      if (this.filter.search) {
        formData.append('search', this.filter.search)
      }
      if (this.filter.themes && this.filter.themes.length) {
        formData.append('subject', JSON.stringify(this.filter.themes))
      }
      if (this.filter.participants && this.filter.participants.length) {
        formData.append('participants', JSON.stringify(this.filter.participants))
      }

      return formData
    },
  },
  methods: {
    onFilter(filter) {
      this.filter = filter

      this.fetchDataHandler(true, false)
    },
    fetchDataHandler(withCancel = true, withNextPage = true) {
      if (withCancel) {
        source.cancel()
        source = CancelToken.source()
      }
      this.fetchData(withNextPage)
    },
    async fetchData(withNextPage) {
      try {
        this.$root.$emit('setLoading', true)
        const data = this.filterFormData
        const nextPageNumber = withNextPage ? this.nextPageNumber : 1

        const options = {
          url: `${this.actionGet}?pageNumber=${nextPageNumber}&PAGEN_1=${nextPageNumber}`,
          method: 'POST',
          data,
          cancelToken: source.token,
        }

        const response = await axios(options);

        if (response.status === 200 || response.statusText === 'OK') {
          this.nextPageNumber = response.data.nextPageNumber
          this.items = withNextPage ? [...this.items, ...response.data.items] : response.data.items
        }
      } catch (error) {
        if (axios.isCancel(error)) {
          const date = new Date()
          const h = date.getHours()
          const m = date.getMinutes()
          const s = date.getSeconds()

          console.warn('Запрос отменен:', `${h}:${m}:${s}`)
        }
      } finally {
        this.$root.$emit('setLoading', false)
      }
    },
    async observerHandler(entries) {
      if (!entries[0].isIntersecting || this.nextPageNumber <= 1) {
        return
      }

      this.fetchDataHandler(false);
    },
    setDefaultFilterDay() {
      this.filter.day = this.filterDays?.length && this.filterDays[0].date
    }
  },
  mounted() {
    this.intersectionObserver = new IntersectionObserver(this.observerHandler);
    this.intersectionObserver.observe(this.$refs.infinityTrigger);
  },
  created() {
    this.setDefaultFilterDay()
  }
}
